-- Performance Indexes Migration\n-- Created: 2026-02-17\n\n-- Index for booking status queries\nCREATE INDEX IF NOT EXISTS idx_pemesanan_status ON pemesanan(status_pemesanan);\n\n-- Index for payment lookups by booking\nCREATE INDEX IF NOT EXISTS idx_pembayaran_pemesanan ON pembayaran(pemesanan_id);\n\n-- Index for reviews by room\nCREATE INDEX IF NOT EXISTS idx_ulasan_kamar ON ulasan(kamar_id);\n\n-- Index for user login queries\nCREATE INDEX IF NOT EXISTS idx_users_email ON users(email);\n\n-- Index for room status queries\nCREATE INDEX IF NOT EXISTS idx_kamar_status ON kamar(status);\n\nCOMMIT;
